// Custom logging link to log all requests
const loggingLink = new ApolloLink((operation, forward) => {
    console.log('========== APOLLO CLIENT REQUEST ==========');
    console.log('Operation Name:', operation.operationName);
    console.log('Query:', operation.query.loc?.source.body);
    console.log('Variables:', JSON.stringify(operation.variables, null, 2));
    console.log('=========================================');
    return forward(operation);
});

// Custom Link to format GraphQL request as string (matching Postman format)
// The PHP endpoint expects the GraphQL query as a STRING in the request body
const customGraphQLLink = new ApolloLink((operation, forward) => {
    // Intercept the generatePaymentHash mutation and format it correctly
    if (operation.operationName === 'generatePaymentHash') {
        const { key } = operation.variables;
        const url = "https://createpayuhashphp.onrender.com/createPayuHashKey.php";
        
        console.log('========== CUSTOM GRAPHQL REQUEST ==========');
        console.log('URL:', url);
        console.log('Hash String:', key);
        
        // Format the GraphQL query as a string (matching Postman format)
        // The hash string should be plain text - only escape quotes for GraphQL string literal
        // Don't escape backslashes as they might be part of the JSON structure
        const escapedKey = key.replace(/"/g, '\\"');
        const graphQLQueryString = `mutation { createPayuHashKey ( hashname: "${escapedKey}") { get_payment_hash }}`;
        
        console.log('========== PAYLOAD DETAILS ==========');
        console.log('Original Hash String:', key);
        console.log('Escaped Hash String:', escapedKey);
        console.log('GraphQL Query String:', graphQLQueryString);
        
        const requestBody = {
            query: graphQLQueryString,
            variables: {}
        };
        
        console.log('Request Body (JSON):', JSON.stringify(requestBody, null, 2));
        console.log('Request Body (raw string):', JSON.stringify(requestBody));
        console.log('=====================================');
        
        const requestPromise = fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody)
        })
        .then(response => {
            console.log('========== SERVER RESPONSE ==========');
            console.log('Status:', response.status);
            console.log('Status Text:', response.statusText);
            console.log('OK:', response.ok);
            
            if (!response.ok) {
                return response.text().then(text => {
                    console.error('Error Response Body:', text);
                    throw new Error(`Server error: ${response.status} ${response.statusText} - ${text}`);
                });
            }
            
            return response.json().then(data => {
                console.log('Response Data:', JSON.stringify(data, null, 2));
                
                // Return the response in Apollo Client format
                return {
                    data: data.data || {
                        createPayuHashKey: {
                            get_payment_hash: data?.get_payment_hash || data?.data?.get_payment_hash
                        }
                    }
                };
            });
        })
        .catch(error => {
            console.error('========== REQUEST ERROR ==========');
            console.error('Error:', error);
            throw error;
        });
        
        // Convert Promise to Observable using ApolloLink's pattern
        // ApolloLink expects an Observable, so we create one manually
        return new Observable(observer => {
            requestPromise
                .then(result => {
                    observer.next(result);
                    observer.complete();
                })
                .catch(error => {
                    observer.error(error);
                });
        });
    }
    
    // For other operations, use the default HTTP link
    return forward(operation);
});

// HTTP Link (fallback for other operations)
const httpLink = new HttpLink({ 
    uri: "https://createpayuhashphp.onrender.com/createPayuHashKey.php"
});

// Initialize Apollo Client OUTSIDE component with logging
const client = new ApolloClient({
    link: ApolloLink.from([loggingLink, customGraphQLLink, httpLink]),
    cache: new InMemoryCache(),
});

// GraphQL Mutation OUTSIDE component
const GENERATE_PAYMENT_HASH = gql`
    mutation generatePaymentHash($key: String!) {
        createPayuHashKey(hashname: $key) {
            get_payment_hash
        }
    }
`;

// Event Queue System for Hash Generation
const eventQueue = [];
let processing = false;

// Function to process hash generation requests
async function processNextEvent() {
    console.log('========== processNextEvent CALLED ==========');
    console.log('Current processing status:', processing);
    console.log('Queue length:', eventQueue.length);
    
    if (processing || eventQueue.length === 0) {
        console.log('â¸ï¸ Skipping - Processing:', processing, '| Queue empty:', eventQueue.length === 0);
        return;
    }

    processing = true;
    console.log('âœ… Starting to process next event');
    
    const event = eventQueue.shift();
    console.log('ðŸ“¦ Event removed from queue');
    console.log('Event details:', JSON.stringify(event, null, 2));
    console.log('Remaining queue length:', eventQueue.length);
    
    const { requestId, ...hashData } = event;
    console.log('Request ID:', requestId);
    console.log('Hash Name:', event.hashName);
    console.log('Hash String:', event.hashString);

    try {
        console.log('ðŸ”„ Calling sendBackHash...');
        const response = await sendBackHash(event.hashName, event.hashString);
        console.log('âœ… sendBackHash completed successfully');
        console.log('Response:', response);
        // NativeModules.PayUBizSdk.onHashGenerated(requestId, response.hash);
    } catch (err) {
        console.error('âŒ Error in processNextEvent');
        console.error('Error:', err);
        console.error('Error message:', err.message);
        // NativeModules.PayUBizSdk.onHashError(requestId, err.message);
    } finally {
        processing = false;
        console.log('ðŸ Processing complete. Setting processing flag to false');
        console.log('Checking for more events in queue...');
        processNextEvent();
    }
    console.log('========== processNextEvent END ==========');
}

